settings.liquid:
{% assign current_variant = product.selected_or_first_available_variant %}
          {% if current_variant.metafields.custom.product_eta contains ':' %}
              {% assign firsthandle = current_variant.metafields.custom.product_eta | split: ":" | first %}
              {% assign secondhandle = current_variant.metafields.custom.product_eta | split: ":" | last %}
          {% endif %}
<div id="hc-new-eta">
{% if current_variant.available == false or current_variant.inventory_quantity < 1 %}
        <input name="{% if current_variant.metafields.custom.product_eta contains ':' %}properties[{{ firsthandle }}]{% else %}properties[Product ETA:]{% endif %}" type="hidden" value="{% if current_variant.metafields.custom.product_eta contains ':' %}{{ secondhandle }}{% else %}{{ current_variant.metafields.custom.product_eta }}{% endif %}" />
{% endif %}
</div>
<script>
      var item_shipping = {};
      var item_inventory = {};
      {% for variant in product.variants %}
         item_shipping[{{ variant.id }}] = {{ variant.metafields.custom.product_eta | json }};
         item_inventory[{{variant.id}}] = {{ variant.inventory_quantity | json }};
      {% endfor %}
    </script>
